import React from 'react';

class App extends React.Component {
  constructor() {
    super()
    this.state = {
      todos: [],
      isLoaded: false,
    }
  }

  componentDidMount() {
    fetch("https://jsonplaceholder.typicode.com/todos")
        .then(response => response.json())
        .then(data => {
            this.setState({
              todos: data,
              isLoaded: true,
            })
        })
  }

  render() {

    var { todos, isLoaded } = this.state;

    if(!isLoaded) {
        return <div>Loading...</div>
    } else {
        return (
          <div>
            <ul>
              {todos.map(todo => (
                <li key={todo.id}>
                    Title: {todo.title} | Completed: {todo.completed === true ? <li>True</li> : <li>False</li>}
                </li>
              ))}
            </ul>
          </div>
        )
    }
  }
}

export default App;
