import React from 'react';

class App extends React.Component {
  constructor() {
    super()
    this.state = {
      todos: [],
      isLoaded: false,
    }
  }

  componentDidMount() {
    fetch("https://jsonplaceholder.typicode.com/todos")
        .then(response => response.json())
        .then(data => {
            this.setState({
              todos: data,
              isLoaded: true,
            })
        })
  }

  render() {

    var { todos, isLoaded } = this.state;

    if(!isLoaded) {
        return <div>Loading...</div>
    } else {
        return (
          <div>
              {todos.map(todo => (
                <div key={todo.id} style={{width: "400px",  overflow: "hidden", textOverflow: "ellipsis", height: "100px", border: "0.1px solid #000", textAlign: "center", verticalAlign: "middle", lineHeight: "100px", backgroundColor: "#FFFF", borderRadius: "10px", marginBottom: "30px", boxShadow: "0px 4px 1px rgba(0, 0, 0, 0.25)", fontFamily: "Montserrat", fontStyle: "normal", fontWeight: "bold", fontSize: "20px"}}>
                    <span className="dot" style={{height: "60px", width: "60px", backgroundColor: "#008F7A", borderRadius: "50%", display: "inline-block", verticalAlign: "middle", lineHeight: "110px", marginRight: "30px"}}></span>
                    {todo.title} {/* | Completed: {todo.completed === true ? <div>True</div> : <div>False</div>} */}
                </div>
              ))}
          </div>
        )
    }
  }
}

export default App;
