import React from 'react';
import './index.css';

class App extends React.Component {
  constructor() {
    super()
    this.state = {
      todos: [],
      isLoaded: false,
    }
  }

  componentDidMount() {
    fetch("https://jsonplaceholder.typicode.com/todos")
        .then(response => response.json())
        .then(data => {
            this.setState({
              todos: data,
              isLoaded: true,
            })
        })
  }

  render() {

    var { todos, isLoaded } = this.state;

    if(!isLoaded) {
        return <div>Loading...</div>
    } else {
        return (
          <div>
              {todos.map(todo => (
                <div key={todo.id} style={{width: "400px",  overflow: "hidden", textOverflow: "ellipsis", whiteSpace: "nowrap", height: "100px", border: "0.1px solid #000", lineHeight: "100px", backgroundColor: "#FFFF", borderRadius: "10px", marginBottom: "20px", boxShadow: "0px 4px 1px rgba(0, 0, 0, 0.25)", fontFamily: "Montserrat", fontStyle: "normal", fontWeight: "bold", fontSize: "20px", position: "relative", left: "0%", right: "0%", top: "0%", bottom: "0%"}}>
                    <span className="dot" style={{height: "60px", width: "60px", backgroundColor: todo.completed ? "#008F7A" : "#EBEBEB", borderRadius: "50%", display: "inline-block", verticalAlign: "middle", lineHeight: "100px", left: "6%", right: "79%", top: "20%", bottom: "20%", position: "absolute"}}></span>
                    <div style = {{position: "absolute", left: "27%", right: "7.25%", top: "38%", bottom: "38%", marginLeft: "20%"}}>{todo.title}</div> {/* | Completed: {todo.completed === true ? <div>True</div> : <div>False</div>} */}
                </div>
              ))}
          </div>
        )
    }
  }
}

export default App;
